</code></pre> <pre><code>&lt;?php

public function up()
{
  Schema::create('alumno_materia', function (Blueprint $table) {
    $table-&gt;id();

    $table-&gt;foreignId('alumno_id')
      -&gt;nullable()
      -&gt;constrained('alumnos')
      -&gt;cascadeOnUpdate()
      -&gt;nullOnDelete();

    $table-&gt;foreignId('materia_id')
      -&gt;nullable()
      -&gt;constrained('materias')
      -&gt;cascadeOnUpdate()
      -&gt;nullOnDelete();

    $table-&gt;timestamps();
  });
}
</code></pre>
